+package com.example.careconnect.repository

+import android.util.Log
+import com.example.careconnect.firestore.ChatMessage
+import com.example.careconnect.firestore.ChatSession
+import com.google.firebase.firestore.FirebaseFirestore
+import com.google.firebase.firestore.Query
+import com.google.firebase.Timestamp
+import kotlinx.coroutines.tasks.await
+import okhttp3.MediaType.Companion.toMediaType
+import okhttp3.OkHttpClient
+import okhttp3.Request
+import okhttp3.RequestBody.Companion.toRequestBody
+import org.json.JSONArray
+import org.json.JSONObject
+import java.util.UUID
+
+class ChatRepository {
+ private val firestore = FirebaseFirestore.getInstance()
+ private val client = OkHttpClient()
+ private val apiKey = "AIzaSyCbh-ey-MPn-wk3ey3yb15Zf-qqWCbca6w"
+ 
+ suspend fun createChatSession(userId: String, firstMessage: String): ChatSession {
+ val sessionId = UUID.randomUUID().toString()
+ val chatSession = ChatSession(
+ id = sessionId,
+ userId = userId,
+ title = firstMessage.take(50) + if (firstMessage.length > 50) "..." else "",
+ lastMessage = firstMessage,
+ lastUpdated = Timestamp.now(),
+ messageCount = 1
+ )
+ 
+ firestore.collection("chatSessions")
+ .document(sessionId)
+ .set(chatSession)
+ .await()
+ 
+ return chatSession
+ }
+ 
+ suspend fun getChatSessions(userId: String): List<ChatSession> {
+ return try {
+ val result = firestore.collection("chatSessions")
+ .whereEqualTo("userId", userId)
+ .orderBy("lastUpdated", Query.Direction.DESCENDING)
+ .get()
+ .await()
+ 
+ result.toObjects(ChatSession::class.java)
+ } catch (e: Exception) {
+ Log.e("ChatRepository", "Error getting chat sessions", e)
+ emptyList()
+ }
+ }
+ 
+ suspend fun getChatMessages(chatId: String): List<ChatMessage> {
+ return try {
+ val result = firestore.collection("chatMessages")
+ .whereEqualTo("chatId", chatId)
+ .orderBy("timestamp", Query.Direction.ASCENDING)
+ .get()
+ .await()
+ 
+ result.toObjects(ChatMessage::class.java)
+ } catch (e: Exception) {
+ Log.e("ChatRepository", "Error getting chat messages", e)
+ emptyList()
+ }
+ }
+ 
+ suspend fun saveMessage(chatMessage: ChatMessage): ChatMessage {
+ val messageId = UUID.randomUUID().toString()
+ val messageWithId = chatMessage.copy(id = messageId)
+ 
+ firestore.collection("chatMessages")
+ .document(messageId)
+ .set(messageWithId)
+ .await()
+ 
+ // Update chat session
+ updateChatSession(chatMessage.chatId, chatMessage.content)
+ 
+ return messageWithId
+ }
+ 
+ private suspend fun updateChatSession(chatId: String, lastMessage: String) {
+ try {
+ val sessionRef = firestore.collection("chatSessions").document(chatId)
+ val sessionDoc = sessionRef.get().await()
+ 
+ if (sessionDoc.exists()) {
+ val currentCount = sessionDoc.getLong("messageCount") ?: 0
+ sessionRef.update(
+ mapOf(
+ "lastMessage" to lastMessage,
+ "lastUpdated" to Timestamp.now(),
+ "messageCount" to currentCount + 1
+ )
+ ).await()
+ }
+ } catch (e: Exception) {
+ Log.e("ChatRepository", "Error updating chat session", e)
+ }
+ }
+ 
+ suspend fun sendToGemini(message: String): String {
+ return try {
+ val requestBody = JSONObject().apply {
+ put("contents", JSONArray().apply {
+ put(JSONObject().apply {
+ put("parts", JSONArray().apply {
+ put(JSONObject().apply {
+ put("text", message)
+ })
+ })
+ })
+ })
+ }
+ 
+ val request = Request.Builder()
+ .url("https://generativelanguage.googleapis.com/v1beta/models/gemini-2.0-flash:generateContent?key=$apiKey")
+ .post(requestBody.toString().toRequestBody("application/json".toMediaType()))
+ .build()
+ 
+ val response = client.newCall(request).execute()
+ val responseBody = response.body?.string()
+ 
+ if (response.isSuccessful && responseBody != null) {
+ val jsonResponse = JSONObject(responseBody)
+ val candidates = jsonResponse.getJSONArray("candidates")
+ if (candidates.length() > 0) {
+ val content = candidates.getJSONObject(0).getJSONObject("content")
+ val parts = content.getJSONArray("parts")
+ if (parts.length() > 0) {
+ return parts.getJSONObject(0).getString("text")
+ }
+ }
+ }
+ 
+ "Sorry, I couldn't process your request at the moment."
+ } catch (e: Exception) {
+ Log.e("ChatRepository", "Error calling Gemini API", e)
+ "Sorry, there was an error processing your request."
+ }
+ }
