+ fun unfollowUser(followerId: Int, followingId: Int) {
+ viewModelScope.launch {
+ try {
+ socialRepository.unfollowUser(followerId, followingId)
+ } catch (e: Exception) {
+ // Handle error
+ }
+ }
+ }
