+ items(searchResults.filter { it.id != currentUser.id }) { user ->
+ UserItem(
+ user = user,
+ currentUser = currentUser,
+ socialViewModel = socialViewModel,
+ onUserClick = onUserClick
+ )
+ }
+ }
