+ /**
+ * Accept a follow request
+ */
+ suspend fun acceptFollowRequest(requestId: String): Boolean {
+ return try {
+ // Get the request first
+ val requestDoc = followRequestsCollection.document(requestId).get().await()
+ val request = requestDoc.toObject(FirestoreFollowRequest::class.java)
+ 
+ if (request != null) {
+ // Update request status
+ followRequestsCollection.document(requestId)
+ .update(
+ mapOf(
+ "status" to "accepted",
+ "acceptedAt" to System.currentTimeMillis()
+ )
+ ).await()
+ 
+ // Create following relationship
+ val followingId = UUID.randomUUID().toString()
+ val following = FirestoreFollowing(
+ id = followingId,
+ followerUid = request.fromUserUid,
+ followerName = request.fromUserName,
+ followerEmail = request.fromUserEmail,
+ followingUid = request.toUserUid,
+ followingName = request.toUserName,
+ followingEmail = request.toUserEmail
+ )
+ 
+ followingCollection.document(followingId).set(following).await()
+ 
+ Log.d(TAG, "Follow request accepted: ${request.fromUserName} now follows ${request.toUserName}")
+ true
+ } else {
+ false
+ }
+ 
+ } catch (e: Exception) {
+ Log.e(TAG, "Error accepting follow request", e)
+ false
+ }
+ }
+ 
+ /**
+ * Reject a follow request
+ */
+ suspend fun rejectFollowRequest(requestId: String): Boolean {
+ return try {
+ followRequestsCollection.document(requestId)
+ .update(
+ mapOf(
+ "status" to "rejected",
+ "rejectedAt" to System.currentTimeMillis()
+ )
+ ).await()
+ 
+ Log.d(TAG, "Follow request rejected")
+ true
+ 
+ } catch (e: Exception) {
+ Log.e(TAG, "Error rejecting follow request", e)
+ false
+ }
+ }
+ 
+ /**
+ * Get users that a user is following
+ */
+ suspend fun getFollowing(userUid: String): List<FirestoreFollowing> {
+ return try {
