+ suspend fun unfollowUser(followerUid: String, followingUid: String): Boolean {
+ return try {
+ val result = followingCollection
+ .whereEqualTo("followerUid", followerUid)
+ .whereEqualTo("followingUid", followingUid)
+ .get()
