+ result.documents.forEach { doc ->
+ doc.reference.delete().await()
+ }
+ 
+ Log.d(TAG, "User unfollowed successfully")
+ true
+ 
+ } catch (e: Exception) {
+ Log.e(TAG, "Error unfollowing user", e)
+ false
