 Icon(Icons.Default.Notifications, contentDescription = "Follow Requests")
 }
 Text("${firestorePendingRequests.size}")
 }
 } else {
 IconButton(onClick = onNavigateToFirestoreRequests) {
 Icon(Icons.Default.Notifications, contentDescription = "Follow Requests")
 }
 }
 }

