@Composable
fun CareConnectApp() {
 val authViewModel: SimpleAuthViewModel = viewModel()
 val authState by authViewModel.authState.collectAsState()

 Surface(modifier = Modifier.fillMaxSize()) {
 when (authState) {
 is SimpleAuthState.Loading -> {
 // Show loading screen while checking authentication
 Box(
 modifier = Modifier.fillMaxSize(),
 contentAlignment = Alignment.Center
 ) {
 CircularProgressIndicator()
 }
 }
 is SimpleAuthState.Authenticated -> {
 // User is signed in, show home screen
 HomeScreen()
 }
 is SimpleAuthState.Unauthenticated, is SimpleAuthState.Error -> {
 // User is not signed in, show auth screen
 SimpleAuthScreen(
 authViewModel = authViewModel,
 onAuthSuccess = {
 // Navigation will be handled automatically by the ViewModel
 }
 )
 }
 }
 }
}

