@Composable
fun LoginScreen(
 authViewModel: FirebaseAuthViewModel = viewModel(),
 onSignUpClick: () -> Unit = {}
) {
 var email by remember { mutableStateOf("") }
 var password by remember { mutableStateOf("") }
 var rememberMe by remember { mutableStateOf(false) }
 var showForgotPassword by remember { mutableStateOf(false) }

 val authState by authViewModel.authState.collectAsState()
 val forgotPasswordState by authViewModel.forgotPasswordState.collectAsState()
 val isLoading = authState is FirebaseAuthState.Loading

 Column(
 modifier = Modifier
 .fillMaxSize()
 .padding(24.dp),
 verticalArrangement = Arrangement.Center,
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Text(
 text = "Log In",
 fontSize = 32.sp,
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.primary
 )

 Spacer(modifier = Modifier.height(24.dp))

 Text("Email", modifier = Modifier.align(Alignment.Start))
 OutlinedTextField(
 value = email,
 onValueChange = { 
 email = it
 authViewModel.resetAuthError()
 },
 singleLine = true,
 modifier = Modifier.fillMaxWidth(),
 enabled = !isLoading
 )

 Spacer(modifier = Modifier.height(16.dp))

 Text("Password", modifier = Modifier.align(Alignment.Start))
 OutlinedTextField(
 value = password,
 onValueChange = { 
 password = it
 authViewModel.resetAuthError()
 },
 visualTransformation = PasswordVisualTransformation(),
 singleLine = true,
 modifier = Modifier.fillMaxWidth(),
 enabled = !isLoading
 )

 Spacer(modifier = Modifier.height(8.dp))

 Row(
 modifier = Modifier
 .fillMaxWidth()
 .padding(vertical = 8.dp),
 horizontalArrangement = Arrangement.SpaceBetween,
 verticalAlignment = Alignment.CenterVertically
 ) {
 Row(
 verticalAlignment = Alignment.CenterVertically
 ) {
 Checkbox(
 checked = rememberMe,
 onCheckedChange = { rememberMe = it },
 enabled = !isLoading
 )
 Text(text = "Remember me")
 }

 Text(
 text = "Forgot password?",
 color = MaterialTheme.colorScheme.secondary,
 modifier = Modifier.clickable(enabled = !isLoading) { 
 showForgotPassword = true 
 }
 )
 }

 // Show auth error
 val currentAuthState = authState
 if (currentAuthState is FirebaseAuthState.Error) {
 Spacer(modifier = Modifier.height(8.dp))
 Text(
 text = currentAuthState.message,
 color = MaterialTheme.colorScheme.error
 )
 }

 Spacer(modifier = Modifier.height(8.dp))

 Button(
 onClick = {
 if (email.isNotBlank() && password.isNotBlank()) {
 authViewModel.login(email, password)
 }
 },
 modifier = Modifier
 .fillMaxWidth()
 .height(48.dp),
 enabled = !isLoading && email.isNotBlank() && password.isNotBlank()
 ) {
 if (isLoading) {
 CircularProgressIndicator(modifier = Modifier.size(16.dp))
 } else {
 Text("Log In")
 }
 }

 Spacer(modifier = Modifier.height(16.dp))

 Text(
 text = "Or",
 fontSize = 14.sp,
 color = Color.Gray
 )

 Spacer(modifier = Modifier.height(8.dp))

 Text(
 text = "Sign Up",
 color = MaterialTheme.colorScheme.secondary,
 modifier = Modifier.clickable(enabled = !isLoading) { onSignUpClick() }
 )
 }

 // Forgot Password Dialog
 if (showForgotPassword) {
 ForgotPasswordDialog(
 onDismiss = { 
 showForgotPassword = false
 authViewModel.resetForgotPasswordState()
 },
 onSendEmail = { emailAddress ->
 authViewModel.forgotPassword(emailAddress)
 },
 forgotPasswordState = forgotPasswordState
 )
 }
}

